clcclearclose allfs = 16000;t1 = 1 : 1/fs : 2;t2 = 3 : 1/fs : 4;t3 = 5 : 1/fs : 6;signal2 = sin(2*pi*697*t1) + 0.3*sin(2*pi*1336*t1);signal4 = sin(2*pi*770*t2) + 0.3*sin(2*pi*1209*t2);signal6 = sin(2*pi*770*t3) + 0.3*sin(2*pi*1477*t3);fx1 = fft(signal2, fs);afx1 = abs(fx1);afx1 = afx1 -1;fx2 = fft(signal4, fs);afx2 = abs(fx2);afx2 = afx2 -1;fx3 = fft(signal6, fs);afx3 = abs(fx3);afx3 = afx3 -1;fdomain = 0:fs/2-1;[magnitude fmax1] = max(afx1);[magnitude fmax2] = max(afx2);[magnitude fmax3] = max(afx3);plot(t1,signal2, 'r')figureplot(t2,signal4, 'r')figureplot(t3,signal6, 'r')figurestem(fdomain, afx1(1:floor(end/2)))figurestem(fdomain, afx1(1:floor(end/2)))figurestem(fdomain, afx1(1:floor(end/2)))