clcclearclose all% Simulation of FFT Methoddisp('CODE: FFT of Sinusoidal Signal');% Sampling ratefs = input('Sampling rate [Hz]:');% Time varying in one secondt = 0:1/fs:1;% Frequency of signalf = input('Frequency of signal [Hz]:');% Sinusoidal signalsignal = sin(2*pi*f*t);% FFT codesxfft = fft(signal, fs);axfft = abs(xfft);% Frequency domainfdomain = 0:fs/2-1;% plot output waveformsubplot(2,1,1);plot(t,signal, 'r')xlabel('Time [s]');ylabel('Amplitude');title('Sampled Signal');subplot(2,1,2);stem(fdomain, axfft(1:floor(end/2)))xlabel('Frequency [Hz]');ylabel('Magnitude');