clcclearclose alldisp('CODE: FFT of Two Sinusoidal Signals');% Sampling ratefs = input('Sampling rate [Hz]:');% Time varying in one secondt = 0:1/fs:1;% Frequency of signal 1stf1 = input('Frequency of signal 1 [Hz]:');% Frequency of signal 2ndf2 = input('Frequency of signal 2 [Hz]:');% Sinusoidal signal with additive noisesignal = sin(2*pi*f1*t) + 0.3*sin(2*pi*f2*t);% FFT codesxfft = fft(signal, fs);axfft = abs(xfft);% Frequency domainfdomain = 0:fs/2-1;% plot output waveformsubplot(2,1,1);plot(t,signal, 'r')xlabel('Time [s]');ylabel('Amplitude');title('Sampled Signal');subplot(2,1,2);stem(fdomain, axfft(1:floor(end/2)))xlabel('Frequency [Hz]');ylabel('Magnitude');